INSERT INTO `paper_student` VALUES ('745', '201421801033', '任慧', '2014年8月', '材料物理与化学', '学硕', '公开', '半导体材料', '过渡金属二硫化合物薄膜的制备与性能研究', '二硫化钼制备和性能研究，特殊形貌异质结构的形成机理，二硫化钼热分解模拟', '2', '67'), ('746', '201521801002', '郭瑞康', '2015年9月', '材料物理与化学', '学硕', '公开', '磁性功能材料', '基于反铁磁半导体的稀磁特性研究', '开发了一种制备具备高居里温度的自旋电子学器件的新思路', '2', '70'), ('747', '201521801003', '王江峰', '2015年9月', '材料物理与化学', '学硕', '公开', '能源与环境材料', '水热法合成镍锰酸锂材料及电化学性能研究', '采用水热法合成具有特殊形貌的高电压镍锰酸锂材料。该方法以尿素为沉淀剂，通过一步水热反应制得粒径分布均匀的Ni0.25Mn0.75CO3前驱体，预烧混锂后经过高温煅烧制得由一次亚微米颗粒组成的多孔结构二次颗粒镍锰酸锂材料。', '2', '69'), ('748', '201521801004', '马悦兴', '2015年9月', '材料物理与化学', '学硕', '公开', '磁性功能材料', '部分铁、镍基Heusler合金中相变材料的设计与开发', '利用第一性原理计算和实验相结合的方法预测并研究了部分具有形状记忆效应的铁、镍基Heusler合金及其原子占位规律，同时研究了其它合金元素（如碳）掺杂对材料马氏体相变及磁性能的影响。', '2', '61'), ('749', '201521801009', '张小强', '2015年9月', '材料物理与化学', '学硕', '公开', '半导体材料研究方向', 'MoS2/VO2纳米复合材料的制备及性能的研究', '选择具有相变功能特性的材料VO2，将其与MoS2纳米片耦合得到结构良好的催化剂，并研究其电催化析氢性能。通过调整实验参数和反应条件深入研究复合材料生长机理以及界面晶格结构变化对HER催化性能的影响，为进一步界面设计及其复合材料的应用提供了理论基础和现实意义。 ', '2', '74'), ('750', '201521801010', '张新', '2015年9月', '材料物理与化学', '学硕', '公开', '半导体材料', '一维有机金属卤化物钙钛矿材料的可控制备及光电性能的研究', '采用两步旋涂工艺研究光电性能与CH3NH3PbI3纳米线尺寸之间的关系；\n通过掺杂溴元素得到溴元素掺杂的CH3NH3Pb(I1-xBrx)3纳米线，提高纳米线光电稳定性。\n', '2', '63'), ('751', '201521801011', '郝红月', '2015年9月', '材料物理与化学', '学硕', '公开', '新型功能材料', 'C、B小半径原子掺杂对Heusler合金磁性及晶体结构的影响', '用C、B小半径原子掺杂Heusler合金', '2', '68'), ('752', '201521801012', '祁一晗', '2015年9月', '材料物理与化学', '学硕', '公开', '半导体材料', 'Cu2ZnSnX4 (X=S, Se)薄膜的溶液法制备及光电性能研究', '1、首次利用多元醇热注入工艺，合成出二元相纳米晶，进一步采用纳米晶墨水工艺制备出了符合化学计量比的CZTSe薄膜；2、采用低毒环保的乙二胺乙二硫醇作为溶剂，成功溶解单质Cu、Zn、Sn、S等单质前驱体，经旋涂硒化后制备出大尺寸、高致密的CZTSSe薄膜。', '2', '65'), ('753', '201521801013', '李少北', '2015年9月', '材料物理与化学', '学硕', '公开', '光电功能材料', '铌酸锂表面折射率的调控及表征', '本工作突破了传统激光直写法不能对折射率实时调控的技术限制，以实时激光直写法实现了铌酸锂表面折射率的精准调控；并采用光控质子交换法对c切和y切铌酸锂样品表面折射率进行了空间选择性调控。', '2', '76'), ('754', '201521801014', '袁晓海', '2015年9月', '材料物理与化学', '学硕', '公开', '新型功能材料', '氮化硼泡沫的制备及性能研究', '首次采用超声波辅助法实现了一维氮化硼纤维的自组装，进而形成三维的氮化硼泡沫。', '2', '60'), ('755', '201521801016', '龚丽园', '2015年9月',